  CREATE OR REPLACE PROCEDURE "SGMP"."MONTH_OR_QUARTERLY" (cus_id in varchar2,year1 in varchar2, quarter in varchar2)
is
v_month_or_quarterly varchar2(100);
v_all_price varchar2(100);

CURSOR query1 is
    select to_char(t10.sale_date,'yyyy-MM') as 월별, to_char(sum(t10.sale_cnt * t20.prod_price),'999,999,999,999') as 구매금액
    from sale t10, product t20, customer t30
    where t10.prod_id = t20.prod_id
    and t10.cust_id = t30.cust_id
    and t30.cust_id = cus_id
    and to_date(t10.sale_date) between to_date(year1||'-01-01') and to_date(year1||'-12-31')
    group by to_char(t10.sale_date,'yyyy-MM'),t30.cust_name
    order by to_char(t10.sale_date,'yyyy-MM'),t30.cust_name;

CURSOR query2 is
    with T as(
    select '1분기' as 분기, to_char(sum(t10.sale_cnt * t20.prod_price),'999,999,999,999') as 구매금액
    from sale t10, product t20, customer t30
    where t10.prod_id = t20.prod_id
    and t10.cust_id = t30.cust_id
    and t30.cust_id = cus_id
    and to_date(t10.sale_date) between to_date(year1||'-01-01') and to_date(year1||'-03-31')
    group by t30.cust_name UNION ALL
    select '2분기' as 분기, to_char(sum(t10.sale_cnt * t20.prod_price),'999,999,999,999') as 구매금액
    from sale t10, product t20, customer t30
    where t10.prod_id = t20.prod_id
    and t10.cust_id = t30.cust_id
    and t30.cust_id = cus_id
    and to_date(t10.sale_date) between to_date(year1||'-04-01') and to_date(year1||'-06-30')
    group by t30.cust_name UNION ALL
    select '3분기' as 분기, to_char(sum(t10.sale_cnt * t20.prod_price),'999,999,999,999') as 구매금액
    from sale t10, product t20, customer t30
    where t10.prod_id = t20.prod_id
    and t10.cust_id = t30.cust_id
    and t30.cust_id = cus_id
    and to_date(t10.sale_date) between to_date(year1||'-07-01') and to_date(year1||'-09-30')
    group by t30.cust_name UNION ALL
    select '4분기' as 분기, to_char(sum(t10.sale_cnt * t20.prod_price),'999,999,999,999') as 구매금액
    from sale t10, product t20, customer t30
    where t10.prod_id = t20.prod_id
    and t10.cust_id = t30.cust_id
    and t30.cust_id = cus_id
    and to_date(t10.sale_date) between to_date(year1||'-10-01') and to_date(year1||'-12-31')
    group by t30.cust_name
    )
    select *
    from T;

begin
    if quarter = '월' then
        DBMS_OUTPUT.PUT_LINE('월별 구매 금액');
        DBMS_OUTPUT.PUT_LINE('=================================================================================');
        open query1;
        loop
        fetch query1 into v_month_or_quarterly, v_all_price;
        exit when query1%NOTFOUND;

        DBMS_OUTPUT.PUT_LINE(v_month_or_quarterly||'|'||v_all_price);
        end loop;
    end if;
   if quarter = '분기' then
        DBMS_OUTPUT.PUT_LINE('분기별 구매 금액');
        DBMS_OUTPUT.PUT_LINE('=================================================================================');
        open query2;
        loop
        fetch query2 into v_month_or_quarterly, v_all_price;
        exit when query2%NOTFOUND;

        DBMS_OUTPUT.PUT_LINE(v_month_or_quarterly||'|'||v_all_price);
        end loop;
    end if;
end month_or_quarterly;

/
