  CREATE OR REPLACE PROCEDURE "SGMP"."QUARTERLY" (year1 in varchar2, quarter in varchar2)
is
v_category varchar2(100);
v_all_price varchar2(100);
v_all_wearing_price varchar2(100);
v_net_profit_price varchar2(100);

CURSOR quarters1 is
    select 
    t20.prod_main_category as 분류, 
    to_char(sum(t10.sale_cnt * t20.prod_price),'999,999,999') as 총판매금액, 
    to_char(sum(t10.sale_cnt * t20.prod_wearing_price),'999,999,999') as 총입고가격, 
    to_char(sum(t10.sale_cnt * t20.prod_price)-sum(t10.sale_cnt * t20.prod_wearing_price),'999,999,999') as 총순이익
